	    unpackHeaders:function (data) {
	        if(data instanceof Array) {
	            return data; //in a sample response, data is already an array. No need to unpack
	            //fix for github #1597
	        }
	        if ((!data) || typeof data !== "string") {
	            return [];
	        }
	        else {
	            var vars = [], hash;
	            var hashes = data.split('\n');
	            var header;

	            for (var i = 0; i < hashes.length; i++) {
	                hash = hashes[i];
	                if (!hash) {
	                    continue;
	                }

	                var loc = hash.search(':');

	                if (loc !== -1) {
	                    var name = $.trim(hash.substr(0, loc));
	                    var enabled=true;
	                    if(name.indexOf("//")==0) {
	                        enabled = false;
	                        name = name.substring(2);
	                    }
	                    var value = $.trim(hash.substr(loc + 1));
	                    header = {
	                        "name":$.trim(name),
	                        "key":$.trim(name),
	                        "value":$.trim(value),
	                        "enabled":enabled,
	                        "description":headerDetails[$.trim(name).toLowerCase()]
	                    };

	                    vars.push(header);
	                }
	            }

	            return vars;
	        }
	    },

